mapper.select:
SELECT s.SUMMONER_LEVEL, s.SUMMONER_TIER, s.SUMMONER_PROFILE
FROM SUMMONER s
WHERE s.SUMMONER_NAME = #{summoner_name}

SELECT COUNT(*)
FROM SUMMONER
WHERE summoner_name = #{summoner_name}

SELECT game_id
FROM SUMMONER_RECENT_GAME
WHERE summoner_name = #{summoner_name}

SELECT game_id
FROM RAWDATA
WHERE participant_name = #{summoner_name}
